import React from 'react'
import './Subtotal.css';
import CurrencyFormat from 'react-currency-format';

function Subtotal() {
    return (
        <div className="subtotal">
            <CurrencyFormat 
                renderText={
                <>
                    <p>Subtotal {0} items : </p>
                    <strong>${0}</strong>
                    <small>
                        <input type="check" /> This order contains a gift 
                    </small>
                </>
                }
            />
        </div>
    )
}

export default Subtotal
