import React, { useEffect } from 'react';
import './Search.css';
import SearchProduct from './SearchProduct';
import { useStateValue } from './StateProvider';

function Search() {
    const [{products, searchTitle},dispatch] = useStateValue();

    // ? Clearing out the Products when leaving Page

    // useEffect(() => {
    //     return () => {
    //         dispatch(
    //             {
    //                 type:'CLEAR_PRODUCTS'
    //             }
    //         )
    //     }
    // },[dispatch])



    // ? filtering the Product

    var newProducts = '';
    for(let i=0;i<products.length;i++){
        newProducts = products[i].title.toLowerCase();
    }
    
    console.log(newProducts);

    var product = products.filter( item => (
        item.title === searchTitle
        ));

        // console.log(searchedProducts,searchTitleL);

    return (
        <div>
                {
                    product.map(item=>(
                        <SearchProduct
                        key={item.id} 
                        id={item.id}
                        title={item.title}
                        image={item.image}
                        price={item.price}
                        ratings={item.ratings}
                        />
                    ))
            }
        </div>
    )
}

export default Search
