import React from 'react';
import CheckoutProduct from './CheckoutProduct';
import './Search.css';
import { useStateValue } from './StateProvider';

function Search() {
    const [{products, searchTitle}] = useStateValue();

    // let title='';
    // products.map(item => title = item.title);
    // console.log('Products : ',products)

    // ? filtering the Products

    var product = products.filter( item => item.title === searchTitle);

    // let example='test match';

    // const testArray=[
    //     {id:1,text:'match'},
    //     {id:2,text:'test match'},
    //     {id:3,text:'text'},
    //     {id:4,text:'sdfsd'}
    // ]

    // let filtered = testArray.filter( test => test.text = example);

    // console.log('filtered : ',filtered);
    
    return (
        <div>
                {
                    
                        <CheckoutProduct 
                        id={product.id}
                        title={product.title}
                        image={product.image}
                        price={product.price}
                        ratings={product.ratings}
                        />
            }
        </div>
    )
}

export default Search
