import React, { useState } from 'react';
import './Payment.css';
import { useStateValue } from './StateProvider';
import { useHistory } from 'react-router-dom';
import CheckoutProduct from './CheckoutProduct';
import CurrencyFormat from 'react-currency-format';
import { CardElement, useElements, useStripe } from '@stripe/react-stripe-js';
import { getBasketTotal } from './reducer';
import axios from 'axios';

function Payment() {

    const history = useHistory();
    const [{basket},dispatch] = useStateValue();

    const stripe = useStripe();
    const elements = useElements();
    // ! Payment Method

    const [succeded, setSucceeded] = useState(false);
    const [processing, setProcessing] = useState(true);

    // ! we use "error" to capture the error if there is some 
    const [error, setError] = useState(null);
    const [disabled, setDisabled] = useState(true);

    const handleSubmit = async (event) =>{
        // ? this function does all the fancy stripe stuff....
        event.preventDefault();
        setProcessing(true);

        // ! Creating paymentMethod
        const {paymentMethod} = await stripe.createPaymentMethod(
            {
                type:'card',
                card:elements.getElement(CardElement)
            }
        )

        if(!error){
            const {uid} = paymentMethod;
            try{              
                const response =  await axios.po  
            }catch(error){
            console.error(error);
            }
        }
    }

    const handleChange = (event) =>{
        // ? this function listens for the changes in the CardElement
        // ? and diplay any errors as the customer types ther card detais.

        setDisabled(event.empty);
        setError(event.error ? event.error.message : "success");
    }

    return (
        <div className="payment">
                <h1 style={{cursor:'pointer'}} align="center" onClick={e=>history.push('/')}>Checkout (<span style={{color:'green',fontWeight:'bold'}}>{basket.length}</span> items)</h1>
            <div className="payment__container">
                {/* Payment Section -> Delivery Address */}
                <div className="payment__section">
                    <div className="payment__title">
                    <h3>Delivery Address</h3>
                    </div> 
                    <div className="payment__address">
                        <p>#147 React Lane</p>
                        <p>California , </p>
                        <p>USA -3290</p>
                    </div>
                </div>   
                {/* Payment Section -> Review Items */}
                <div className="payment__section">
                    <div className="payment__title">
                        <h3>Review items and delivery</h3>
                    </div>

                    <div className="payment__items">
                        {basket.map(item=>(
                        <CheckoutProduct 
                        id={item.id}
                        title={item.title}
                        image={item?.image}
                        price={item.price}
                        ratings={item.ratings}
                        /> 
                        ))}
                    </div>
                </div>
                {/* Payment Section -> Payment Method */}
                <div className="payment__section">
                    <div className="payment__title">
                        <h3>Payment Method</h3>
                    </div>
                    <div className="payment__details">
                        {/* Stripe magic will go here */}

                        <form onSubmit={handleSubmit}>
                                <CardElement onChange={handleChange} />

                            <div className="payment__priceCantainer">
                                <CurrencyFormat 
                                    renderText={(value) =>(
                                        <h3>Order Total : {value}</h3>
                                    )}
                                    decimalScale={2}
                                    value={getBasketTotal(basket)}
                                    displayType={"text"}
                                    thousandSeperator={true}
                                    prefix={"$"}
                                />
                                <button disabled={processing || disabled || succeded}>
                                    <span>{processing ? <p>Processing</p> : "buy Now"}</span>
                                </button>
                            </div>
                            {/* Errors*/}
                            {error && <div>{error}</div>}
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Payment
