import React from 'react';
import './Search.css';
import { useStateValue } from './StateProvider';

function Search() {
    const [{basket, searchTitle}] = useStateValue();

    let title='';
    basket.map(item => title = item.title)

    return (
        <div>
            {
                title = searchTitle 
            }
        </div>
    )
}

export default Search
