import React from 'react';
import './Payment.css';
import { useStateValue } from './StateProvider';

function Payment() {
    const [{basket},dispatch] = useStateValue();
    return (
        <div className="payment">
            <h1 >Checkout ({basket.length}) items</h1>
        </div>
    )
}

export default Payment
