import React, { useEffect } from 'react';
import './Search.css';
import SearchProduct from './SearchProduct';
import { useStateValue } from './StateProvider';

function Search() {
    const [{products , searchTitle},dispatch] = useStateValue();

    // ? Clearing out the Products when leaving Page

    // useEffect(() => {
    //     return () => {
    //         dispatch(
    //             {
    //                 type:'CLEAR_PRODUCTS'
    //             }
    //         )
    //     }
    // },[dispatch])

    // ? filtering the Product

    console.log(searchTitle);

    var product = products.filter( (item) => {
        console.log(item.title.includes(searchTitle));
        console.log(searchTitle);
        return item.title.includes(searchTitle)
    });

    console.log(product);

    return (
        <div>
                {
                    product.map(item=>(
                        <SearchProduct
                        key={item.id} 
                        id={item.id}
                        title={item.title}
                        image={item.image}
                        price={item.price}
                        ratings={item.ratings}
                        />
                    ))
            }
        </div>
    )
}

export default Search
