import React, { useEffect } from 'react';
import './Search.css';
import SearchProduct from './SearchProduct';
import { useStateValue } from './StateProvider';

function Search() {
    const [{products , searchTitle},dispatch] = useStateValue();

    // ? Clearing out the Products when leaving Page

    useEffect(() => {
        return () => {
            dispatch(
                {
                    type:'CLEAR_PRODUCTS'
                }
            )
        }
    },[dispatch])

    const index = products.findIndex((item , ind)=>{
        return item.title === searchTitle
    })

    console.log(index)

    // ? filtering the Product

    var product = products.filter( item => (
        item === searchTitle
        ));

        // console.log(searchedProducts,searchTitleL);

    return (
        <div>
                {
                    product.map(item=>(
                        <SearchProduct
                        key={item.id} 
                        id={item.id}
                        title={item.title}
                        image={item.image}
                        price={item.price}
                        ratings={item.ratings}
                        />
                    ))
            }
        </div>
    )
}

export default Search
